mapper.update:
update ms_admin
set admin_pwd = #{newPwd,jdbcType=VARCHAR}
where admin_number = #{adminNumber,jdbcType=VARCHAR}

mapper.insert:
insert into ms_admin (id, admin_number, admin_pwd, 
  admin_name, login_pre_time, del_flg, identity_id
  )
values (#{id,jdbcType=INTEGER}, #{adminNumber,jdbcType=VARCHAR}, #{adminPwd,jdbcType=VARCHAR}, 
  #{adminName,jdbcType=VARCHAR}, now(), 1, #{identityId,jdbcType=INTEGER}
  )

mapper.select:
select 

from ms_admin
where del_flg != 0
	AND admin_number = #{adminNumber,jdbcType=VARCHAR}
	AND admin_pwd = #{adminPwd,jdbcType=VARCHAR}

select
from ms_admin where del_flg=1
and admin_number like concat(concat('%',#{adminNumber,jdbcType=VARCHAR}),'%')
and admin_name like concat(concat('%',#{adminName,jdbcType=VARCHAR}),'%')
and identity_id = #{identity,jdbcType=INTEGER}
limit #{start},#{size}
